    public boolean setUserReadStatus(User user, ReadStatus status, Date date, boolean notify) {
        ReadReceiptUserLink link = linkForUser(user);

        if (sender.isMe()) {
            isRead = true;
        }

        if(!isRead && status.is(ReadStatus.read())) {
            isRead = true;
            ChatSDK.db().update(this);
        }

        if (link == null || link.getStatus() < status.getValue()) {

            if (!user.isMe()) {
                if (status.is(ReadStatus.read())) {
                    isRead = true;
                } else {
                    isRead = false;
                }
                ChatSDK.db().update(this);
            }

            if (link == null) {
                link = new ReadReceiptUserLink();
                link.setMessageId(getId());
                link.setUserId(user.getId());
                try {
                    ChatSDK.db().getDaoCore().createEntity(link);
                    resetReadReceiptLinks();
                } catch (Exception e) {
                    Logger.info("Read receipt already exists");
                    return false;
                }
            }
            link.setStatus(status.getValue());
            link.setDate(date);
            ChatSDK.db().update(link);
            if (notify) {
                ChatSDK.events().source().accept(NetworkEvent.messageReadReceiptUpdated(this));
            }
            return true;
        }
        return false;
    }

