    /**
     * We can resend if sending or if upload failed
     * @return
     */
    public boolean canResend() {

        if (!getSender().isMe()) {
            return false;
        }

        MessageSendStatus status = getMessageStatus();

        if (status == MessageSendStatus.Failed) {
            return true;
        }
        return uploadFailed();
    }

    /**
     * Upload can fail with a known status of upload failed or if uploading is interrupted and
     * stuck in the uploading state. In that case, we check each file and
     * @return
     */
    public boolean uploadFailed() {
        MessageSendStatus status = getMessageStatus();
        if (status == MessageSendStatus.Failed) {
            return true;
        }
        else if(status == MessageSendStatus.Uploading) {
            if (ChatSDK.upload() != null) {
                // Check if the task is active...
                List<CachedFile> files = ChatSDK.uploadManager().getFiles(getEntityID());
                for (CachedFile file: files) {
                    TransferStatus fileTransferStatus = file.getTransferStatus();
                    if (fileTransferStatus == TransferStatus.Complete) {
                        continue;
                    }
                    if (fileTransferStatus == TransferStatus.Failed) {
                        return true;
                    } else {
                        double age = file.ageInSeconds();
                        if (fileTransferStatus == TransferStatus.Initial && age < 10) {
                            continue;
                        }
                        TransferStatus us = ChatSDK.upload().uploadStatus(file.getHash());
                        if (age > 10 && us == TransferStatus.None) {
                            return true;
                        }
                        if (us == TransferStatus.Failed) {
                            return true;
                        }
                    }
                }
            }
        }
        return false;
    }

